import com.cj.mps3.entity.JobOrder;
import java.util.List;
import java.util.Map;
import xyz.elidom.sys.entity.User;
import xyz.elidom.util.ValueUtil;
import xyz.elidom.util.DateUtil;

def dcCd = params.get('dc_cd');
def jobBatchSeq = params.get('job_batch_seq') == null ? '0': ValueUtil.toString(params.get('job_batch_seq'));
def jobDate = params.get('job_date');
def regionCd = params.get('region_cd');
def skuCd = params.get('sku_cd');

def items = [];

def header = [
	release_date: jobDate,
	print_date: DateUtil.todayStr(),
	doc_title: "제품별 매장 리스트(결품)",
	t_release_date: "출고일자",
	t_barcode: "바코드",
	t_sku_cd: "제품코드",
	t_print_date: "출력일자",
	t_page: "페이지",
	t_sku_nm: "제품명",
	t_region: "호기",
	t_rack: "랙",
	t_seq: "SEQ",
	t_cust_cd: "매장코드",
	t_cust_nm: "매장명",
	t_pick_qty: "지시량",
	t_picked_qty: "피킹량"
];

// 센터 코드 · 작업 일자 · 작업 차수 입력 필수
if(dcCd != null && jobDate != null && ValueUtil.isNotEmpty(jobBatchSeq) && jobBatchSeq != '0') {
	def whereCond_regionCd = ValueUtil.isEmpty(regionCd) ? "" : " AND REGION_CD = '${regionCd}'";
	def whereCond_xRegionCd = ValueUtil.isEmpty(regionCd) ? "" : " AND x.REGION_CD = '${regionCd}'";
	def whereCond_xSkuCd = ValueUtil.isEmpty(skuCd) ? "" : "AND x.SKU_CD = '${skuCd}'";

	def sql = """
		with temp_region as (
		    select x.region_cd, x.region_nm, rank() over (order by x.region_cd asc) as region_no, y.status
		      from tb_region x
		         , (
		            select region_cd, status
		              from tb_job_batch
		             where domain_id = '${domain_id}'
		               and job_date = '${jobDate}'
		               and job_batch_seq = ${jobBatchSeq}
		               and dc_cd = '${dcCd}'
		               and status in ('RUN','END')
		               ${whereCond_regionCd}
		           ) y
		     where x.region_cd = y.region_cd(+)
		       and x.domain_id = '${domain_id}'
		),
		temp_loc as (
		    select x.region_cd, x.region_nm, x.region_no, y.loc_cd, decode(y.side_cd, 'F', 1, 2) as side_no
		      from temp_region x, tb_location y
		     where x.region_cd = y.region_cd
		       and y.domain_id = '${domain_id}'
		           ${whereCond_xRegionCd}
		),
		temp_process as (
		     select x.region_cd, x.com_cd, x.loc_cd, x.cust_cd, x.cust_nm, x.sku_cd, z.sku_nm, z.sku_barcd, nvl(x.process_seq, 0) as process_seq
		       from tb_process x
		          , (select region_cd, status from temp_region where status is not null) y
		          , tb_sku z
		      where x.region_cd = y.region_cd
		        and x.com_cd = z.com_cd
		        and x.sku_cd = z.sku_cd
		        and x.domain_id = '${domain_id}'
		        and x.job_date = '${jobDate}'
		        and x.job_batch_seq = ${jobBatchSeq}
		        and x.dc_cd = '${dcCd}'
		    		${whereCond_xSkuCd}
			    	${whereCond_xRegionCd}
		        and ((y.status = 'RUN' AND x.status = 'C') OR (y.status = 'END' and x.pick_qty != x.picked_qty))
		),
		temp_process_order as (
		    select com_cd, loc_cd, cust_cd, sku_cd, sum(pick_qty) as pick_qty, sum(picked_qty) as picked_qty
		      from tb_process
		      where domain_id = '${domain_id}'
		        and job_date = '${jobDate}'
		        and job_batch_seq = ${jobBatchSeq}
		        and dc_cd = '${dcCd}'
		        and (region_cd , loc_cd, com_cd, sku_cd) in (select region_cd, loc_cd, com_cd, sku_cd from temp_process)
		      group by com_cd, loc_cd, cust_cd, sku_cd
		)
		select z.region_nm  as block
		     , z.side_no as rack
		     , x.process_seq as seq
		     , x.sku_cd
		     , x.sku_nm
		     , x.sku_barcd
		     , x.cust_cd, x.cust_nm, y.pick_qty, nvl(y.picked_qty, 0) as picked_qty
		  from temp_process x
		     , temp_process_order y
		     , temp_loc z
		 where x.loc_cd = y.loc_cd
		   and x.com_cd = y.com_Cd
		   and x.cust_cd = y.cust_cd
		   and x.sku_cd = y.sku_cd
		   and x.region_cd = z.region_cd
		   and x.loc_cd = z.loc_cd
		 order by x.sku_nm, z.region_no, z.side_no, x.process_seq, x.cust_nm
	""";

	queryDsl.init('com.cj.mps3.entity.JobOrder');
	items = queryDsl.selectSql(sql,0,0);
}

if(ValueUtil.isEmpty(items)) {
	items = [[
		block: ' ',
		rack: ' ',
		seq: null,
		cust_cd: ' ',
		cust_nm: ' ',
		pick_qty: null,
		picked_qty: null,
		sku_cd: ' ',
		sku_barcd: ' ',
		sku_nm: ' '
	]];
}

def map = [
	header: header,
	items: items
];

return map;

