def order = orders[0];
def vehicleNo = order['vehicle_no'];
def allocNo = order['alloc_no'];
def passStopNm = order['pass_stop_nm'];
def dockNo = order['pack_type'];
def items = "";
def lineHeight = 293;
def pageIdx = printPage ? (printPage - 1) : 0;
pageIdx = pageIdx * 10;

orders.eachWithIndex { o, i ->
    def idx = pageIdx +  (i + 1);
    def subSkuNm = o.sku_nm;
    def subSkuCd = o.sku_barcd;
    if(subSkuCd.length() > 6) {
    	subSkuCd = subSkuCd.substring(0, 6) + "..";
    }
    if(subSkuNm.length() > 11) {
    	subSkuNm = subSkuNm.substring(0, 11) + "..";
    }
    
    def item = """
^FO39,${lineHeight}
^A1N,30,30
^FD${idx}
^FS

^FO91,${lineHeight}
^A1N,30,30
^FD${subSkuCd}
^FS

^FO268,${lineHeight}
^A1N,30,30
^FD${subSkuNm}
^FS

^FO653,${lineHeight}
^A1N,30,30
^FD${o.picked_qty} EA
^FS
"""
    items += item
    lineHeight += 30;
}

def data = [
	boxId : box.getBoxId(),
	date : box.getOrderDate(),
	vehicleNo : vehicleNo,
	dockNo : dockNo == null ? ' ' : '(' + dockNo + ')',
