// 센터 코드 · 작업 일자 · 작업 차수 입력 필수
if(dcCd != null && jobDate != null && ValueUtil.isNotEmpty(jobBatchSeq) && jobBatchSeq != '0') {
	def whereCond_zoneCd = ValueUtil.isEmpty(zoneCd) ? "" : "AND STORE_ZONE_CD = '${zoneCd}'";

	def regions = [
		"DM03-01",
		"DM03-02",
		"DM03-03",
		"DM03-04",
		"DM03-05",
		"DM03-06",
		"DM03-07",
		"DM03-08",
		"DM03-09",
		"DM03-10"
	];

	def selQry = "", innerSelQry = "";
	regions.eachWithIndex { region, idx, idxPlus = idx + 1 ->
		selQry += ", DECODE(QTY_${idxPlus}, NULL , ' ', TRUNC(QTY_${idxPlus} / BOX_IN_QTY , 0) || '/' || MOD(QTY_${idxPlus}, BOX_IN_QTY)) AS REGION_BOX_EA_${idxPlus}";
		innerSelQry += ", (SELECT PICK_QTY FROM TEMP_ORDER WHERE REGION_CD = '${region}' AND SKU_CD = T_ORDER.SKU_CD) AS QTY_${idxPlus}";
	}

	def sql = """
		WITH TEMP_ORDER AS (
		    SELECT SKU_CD, MAX(SKU_NM) AS SKU_NM, MAX(BOX_IN_QTY) AS BOX_IN_QTY, SUM(ORDER_QTY) AS PICK_QTY, SKU_CLASS, REGION_CD
		      FROM (SELECT X.*, '1' AS SKU_CLASS
		              FROM TB_IF_ORDER X
		             WHERE (X.DOMAIN_ID, X.BATCH_ID) in (
		                               SELECT DOMAIN_ID, ID
		                                 FROM TB_JOB_BATCH
		                                WHERE DOMAIN_ID = ${domain_id}
		                                  AND JOB_BATCH_SEQ = ${jobBatchSeq}
		                                  AND JOB_DATE = '${jobDate}'
		                                  AND DC_CD = '${dcCd}'
		                                  AND STATUS = 'RUN'
		                               )
		            	   ${whereCond_zoneCd}
		           )
		     GROUP BY COM_CD, SKU_CLASS, SKU_CD, REGION_CD
		     ORDER BY COM_CD, SKU_CLASS
		) 
		SELECT SKU_CD, SKU_NM, SKU_CLASS, TRUNC(SKU_SUM / BOX_IN_QTY , 0) || '/' || MOD(SKU_SUM, BOX_IN_QTY) AS TOTAL_QTY
		${selQry}
		  FROM (
		        SELECT SKU_CD, SKU_CLASS, MAX(SKU_NM) AS SKU_NM, MAX(BOX_IN_QTY) AS BOX_IN_QTY, SUM(PICK_QTY) AS SKU_SUM
		${innerSelQry}
		          FROM TEMP_ORDER T_ORDER
		         GROUP BY SKU_CD, SKU_CLASS
		       )
		 ORDER BY SKU_CLASS, SKU_NM
	""";

	queryDsl.init('com.cj.mps3.entity.JobOrder');
	items = queryDsl.selectSql(sql,0,0);
}

if(ValueUtil.isEmpty(items)) {
	items = [[
		sku_class: ' ',
